INSERT INTO alcohols (name, ingredients, proof, description, category, flavors, type, image) VALUES
('Stella Artois', 'Water, Malted Barley, Maize, Saaz Hops', 10, 'This beer is great for its herbal and malty sweetness, with a smooth yet bitter aftertaste. One of the more popular beers to order.', 'beer', 'Bitter', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/beer/stella-artois.jpg'),
('Heineken Beer', 'Water, Malted Barley, Hop Extract, Yeast', 10, 'A sweet and sour beer that often has a hint of green apple and sweet corn. A good beer to sit back and drink with friends.', 'beer', 'Bitter', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/beer/heineken.jpg'),
('Sam Adams', 'Water, Malted Barley, Oats, Wheat, Hops, Yeast', 10, 'We recommend this beer for those who want that toasty, earthy bitter kick for their palette. Rich and toasty.', 'beer', 'Bitter', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/beer/sam-adams.jpg'),
('Sangria', 'Red Wine, Brandy, Oranges, Lemons, Limes, Sugar', 20, 'Sweet, mildly spicy, and very fruity iced drink. Great for those looking for a delicious and hard-hitting drink.', 'wine', 'Bitter, Sweet', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/wine/sangria.jpg'),
('Mimosa', 'White Wine, Orange Juice, Orange Slice', 20, 'A crisp, bubbly and sweet-citrus wine that will make you feel fancy.', 'wine', 'Bitter', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/wine/mimosa.jpg'),
('Mulled Wine', 'Red Wine, Brandy, Orange Juice, Cinnamon, Nutmeg, Sugar', 14, 'A red wine that''s fruity, tarty, and smokey all in one.', 'wine', 'Bitter, Dry', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/wine/mulled-wine.jpg'),
('Samuel Smith''s Organic Cider', 'Water, Organic Apple Concentrate, Organic Cane Sugar, Malic Acid, Yeast', 10, 'A taste similar to sweet apples, but is generally watered down for those looking to have a subtle drink.', 'cider', 'Sweet, Bland', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/cider/samuel-smiths.jpg'),
('Ace Joker Cider', 'Fermented Juice, Carbonated Water, Apple Concentrate, Malic Acid, Sulfite (Preservative)', 14, 'A dry cider made primarily with apple juice, and is allegedly popular with beer and wine enthusiasts.', 'cider', 'Tangy, Dry', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/cider/ace-joker.jpg'),
('Mead', 'Honey, Water, Cinnamon Sticks (Optional)', 20, 'Mead is the classic, medieval drink. This drink is not usually ordered on its own (unless you''re at a Renaissance Fair), but it is served as a foundation for some cocktails. On its own, Mead can taste sweet or dry.', 'mead', 'Sweet, Dry', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/mead/mead.jpg'),
('Saké', 'Rice, Water, Yeast, Koji', 34, 'Saké is a Japanese alcoholic beverage that tastes similar to White Wine, with a dry taste and smooth feel. Some flavors of Saké can also be fruity and sweet. Because this drink tastes so good, it''s easy to get drunk with.', 'sake', 'Dry, Sweet', 'fermented', 'https://worth-a-shot.netlify.app/images/drinks/fermented/sake/sake.jpg'),
('Gin Martini', 'Gin, Dry Vermouth, Lemon Garnish', 60, 'A classic Gin beverage, this drink tastes very strong and bitter, with a slight hint of sweetness. If ordered ''neat'', you''re asking for your alcohol to be poured straight from the bottle (sans chilling).', 'gin', 'Bitter', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/gin/gin-martini.jpg'),
('Tom Collins', 'Gin, Lemon Juice, Sugar, Carbonated Water, Lemon Slice Garnish, Maraschino Cherry', 26, 'With lemon as its primary fruit ingredient, this drink tastes a lot like an alcoholic lemonade. Citrusy, fizzy, and refreshing!', 'gin', 'Sour', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/gin/tom-collins.jpg'),
('Negroni', 'Gin, Vermouth Russo, Campari, Orange Peel Garnish', 48, 'Mostly bitter, but has sweet, fruity, and herbal notes. This drink is said to ''open up'' the appetite, which makes it a great pre-dinner beverage.', 'gin', 'Bitter', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/gin/negroni.jpg'),
('Old Fashioned Brandy', 'Brandy, Angostura Bitter, Orange Slices, Maraschino Cherries, Sugar Cube, Sprite or Club Soda, Orange Slice Garnish', 54, 'The Old Fashioned Brandy has a good bitter flavor, and can be made sweet or sour depending on the soda. A great drink for those looking to have a refined experience.', 'brandy', 'Sweet, Sour', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/brandy/old-fashioned-brandy.jpg'),
('Brandy Alexander', 'Cognac, Creme de Cacoa, Heavy Cream, Grated Nutmeg Garnish', 38, 'This dessert drink is a spinoff of the Alexander. It is chilled, and tastes sweet, creamy, and smooth. It''s like drinking an alcoholic milkshake!', 'brandy', 'Sweet', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/brandy/brandy-alexander.jpg'),
('Sidecar', 'Cognac, Cointreau (Orange Liqueor), Lemon Juice', 48, 'A Sidecar usually tastes sour or citrusy, (due to the Lemon Juice), with a slightly sweet flavor. A nice, tart beverage to get you feeling good.', 'brandy', 'Sour, Tangy', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/brandy/sidecar.jpg'),
('Old Fashioned Whiskey', 'Whiskey, Bitters, Water, Ice, Simple Syrup, Orange Slice Garnish', 54, 'A sweet whiskey cocktail with slight bitterness and spiciness. A whiskey classic.', 'whiskey', 'Sweet, Spicy', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/whiskey/old-fashioned-whiskey.jpg'),
('Whiskey Sour', 'Bourbon Whiskey, Egg Whites, Lemon Juice, Gomme Syrup', 30, 'Whiskey Sours are, as the name suggests, very sour. Some bars will vary on the severity of the sourness, but this is a great alternative to the overly sweet or bitter drinks available at most bars.', 'whiskey', 'Sour, Tangy', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/whiskey/whiskey-sour.jpg'),
('Manhattan', 'Whiskey, Sweet or Dry Red Vermouth, Aromatic Bitters, Cherry Garnish', 60, 'A Manhattan is yet another classic, fancy drink. It can be sweet or dry depending on the vermouth, with a hint of bitter and herbal undertones.', 'whiskey', 'Dry, Bitter', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/whiskey/manhattan.jpg'),
('Daiquiri', 'Rum, Lime Juice, Syrup or Sugar', 40, 'A classic, frozen drink that will generally have a good balance of sweet and sour flavors. The rum in this drink will taste watered down, so it is a good beverage for those looking to have a strong drink with subtle alcohol.', 'rum', 'Sweet, Sour', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/rum/daiquiri.jpg'),
('Piña Colada', 'Rum, Coconut Cream, Pineapple Juice', 26, 'A frozen, dessert drink from Puerto Rico, a Piña Colada is a sweet and delicious drink with a decent alcohol percentage. A must-have beverage for those who have never tried alcohol before. Can be served as a virgin (no alcohol), or with Vodka instead of Rum. This is one of our dev''s favorite drinks!', 'rum', 'Sweet', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/rum/pina-colada.jpg'),
('Mojito', 'Rum, Lime Juice, Mint Leaves, Sugar, Club Soda', 26, 'Mojitos originate from Cuba, and often have a Minty, fresh, and sweet flavor. This drink is bubbly and refreshing, and will make you feel like you''re vacationing on a warm, summer day.', 'rum', 'Sweet, Minty', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/rum/mojito.jpg'),
('Margarita', 'Tequila, Cointreau, Lime Juice', 33, 'Margaritas are citrusy and smooth to drink, and some glasses have salt on the rim. This drink is said to have the perfect balance of sour, sweet, bitter, and salty.', 'tequila', 'Sweet, Sour, Bitter', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/tequila/margarita.jpg'),
('El Diablo', 'Tequila, Crème de cassis, Lime Juice, Ginger Beer', 10, 'The El Diablo is a Tequila Mule that has a rereshing and bold flavor. It is described as earthy and zingy thanks to the lime juice and ginger beer.', 'tequila', 'Sour, Bitter', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/tequila/el-diablo.jpg'),
('Tequila Sunrise', 'Tequila, Grenadine Syrup, Orange Juice, Orange or Cherry Garnish', 26, 'A Tequila Sunrise is a must-have tequila drink. It''s bright and sweet, with a burst of tang. If you''re looking for something fresh and fruity, definitely order this on your next night out.', 'tequila', 'Tangy, Sweet', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/tequila/tequila-sunrise.jpg'),
('Screwdriver', 'Vodka, Orange Juice', 10, 'Yes, it really IS just Vodka and orange juice. A Screwdriver is a simple and class Vodka cocktail, and the perfect drink to order if you want something simple. It''s sometimes bitter, and usually sweet and tangy.', 'vodka', 'Sweet, Tangy', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/vodka/screwdriver.jpg'),
('Cosmopolitan', 'Vodka, Cranberry Juice', 54, 'Cosmopolitans are a good mix of flavors. It''s sweet, fresh, sour, and a little bitter from the Vodka. This drink is great if you''re looking for a light and tangy experience.', 'vodka', 'Sweet, Tangy, Sour', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/vodka/cosmopolitan.jpg'),
('Sea Breeze', 'Vodka, Grapefruit Juice, Cranberry Juice', 20, 'This drink is another refreshing, fruity cocktail. The grapefruit and cranberry juices give the Sea Breeze a nice sweet and sour combination of flavor.', 'vodka', 'Sweet, Sour', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/vodka/sea-breeze.jpg'),
('Death in the Afternoon', 'Absinthe, Champagne', 25, 'Don''t let the name scare you. This drink, also known as the Hemingway, is a dry and bitter drink that tastes a lot like black licorice. This is a great introduction to absinthe-based cocktails.', 'absinthe', 'Dry, Bitter', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/absinthe/death-in-the-afternoon.jpg'),
('Necromancer', 'Absinthe, Elderflower Liqueur, Lillet Blanc, Lemon Juice, Gin', 70, 'Antithesis to its name, the Necromancer is a cocktail that tastes sweet and delicate due to to its floral ingredients, and will reset your palette to wake you right up! Be aware of its high alcohol percentage!', 'absinthe', 'Sweet', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/absinthe/necromancer.jpg'),
('Last Resort', 'Absinthe, Massenez Poire Williams, Lime Juice, Syrup, Egg Whites, Grated Nutmeg Garnish', 48, 'A spin on the Last Word, this drink is floral and refreshing. Although this drink is fruity, it does have a strong kick due to the absinthe.', 'absinthe', 'Sweet', 'distilled', 'https://worth-a-shot.netlify.app/images/drinks/distilled/absinthe/last-resort.jpg');
